class Solution:
    def isPalindrome(self, i, j, s):
        while i < j:
            if s[i] != s[j]:
                return False
            i += 1
            j -= 1
        return True

    def helper(self, i, s, dp):
        if i == len(s):
            return 0
        if dp[i] != -1:
            return dp[i]

        minCost = float('inf')
        for j in range(i, len(s)):
            if self.isPalindrome(i, j, s):
                cost = 1 + self.helper(j + 1, s, dp)
                minCost = min(minCost, cost)

        dp[i] = minCost
        return dp[i]
        
    def minCut(self, s):
        if s=="":return 1
        dp = [-1] * len(s)
        return self.helper(0, s, dp) - 1

